var anexos = {
	buscaPasta: function (descricaoPasta, pastaRaiz) {
		var cPasta = DatasetFactory.createConstraint("documentDescription", descricaoPasta, descricaoPasta, ConstraintType.MUST);
		var cPastaPai = DatasetFactory.createConstraint("parentDocumentId", pastaRaiz, pastaRaiz, ConstraintType.MUST);
		var cActive = DatasetFactory.createConstraint("activeVersion", true, true, ConstraintType.MUST);

		var dsPasta = DatasetFactory.getDataset("document", null, [cPasta, cPastaPai, cActive], null);

		if (dsPasta != null && dsPasta.rowsCount > 0) {
			
			log.info("descricaoPasta 	-> "+descricaoPasta );
			log.info("documentId 		-> "+dsPasta.getValue(0, "documentPK.documentId") );
			
			return dsPasta.getValue(0, "documentPK.documentId");
		} else {
			return anexos.criaPasta(descricaoPasta, pastaRaiz);
		}

	},

	criaPasta: function (txtDescricao, pastaPai) {

		var supplierService 	= ServiceManager.getService('ECMFolderService');
		var serviceHelper 		= supplierService.getBean();
		var serviceLocator 		= serviceHelper.instantiate('com.totvs.technology.ecm.dm.ws.ECMFolderServiceService');
		var service 			= serviceLocator.getFolderServicePort();
		
		var customClient = serviceHelper.getCustomClient(
			service, 
			"com.totvs.technology.ecm.dm.ws.FolderService", 
			{
				"disable.chunking"	: "true",
				"log.soap.messages"	: "true",
				"receive.timeout"	: "60000"
			}
		);

		var retorno = customClient.createSimpleFolder(
			hAPI.getAdvancedProperty("username"),				//username
			hAPI.getAdvancedProperty("password"),				//password
			getValue("WKCompany"),								//companyId
			parseInt(pastaPai),									//parentDocumentId
			hAPI.getAdvancedProperty("publisherId"),			//publisherId
			txtDescricao										//documentDescription
		);

		return retorno.getItem().get(0).getDocumentId();
	},

	publica: function (idParent) {
		var docs = hAPI.listAttachments();
		
		for (var i = 0; i < docs.size(); i++) {
			var doc = docs.get(i);
			
			var descricaoFile = doc.getDocumentDescription();

			doc = anexos.handleDocDto(doc, idParent, descricaoFile)

			hAPI.publishWorkflowAttachment(doc);

		}
		
	},
	
	handleDocDto: function (doc, idParent, descricaoFile) {
		var calendar = java.util.Calendar.getInstance().getTime();
		
		doc.setParentDocumentId(parseInt(idParent));
		doc.setVersionDescription("Processo: " + getValue("WKNumProces"));
		doc.setExpires(false);
		doc.setCreateDate(calendar);
		doc.setInheritSecurity(true);
		doc.setTopicId(1);
		doc.setUserNotify(false);
		doc.setValidationStartDate(calendar);
		doc.setVersionOption("0");
		doc.setUpdateIsoProperties(true);
		if (descricaoFile != '')
			doc.setDocumentDescription(descricaoFile);

		return doc
	}
}

function servicetask30(attempt, message) {
    tools.log("--servicetask30");

    var dataSource = "jdbc/AppDS";
    var ic = new javax.naming.InitialContext();
    var ds = ic.lookup(dataSource);
    var ciclo_atual = hAPI.getCardValue("ciclo_atual");
    var C8_NUM = hAPI.getCardValue("C8_NUM");
    var idEmpresa = hAPI.getCardValue("idEmpresa");
    var docId = hAPI.getCardValue("numIdCot");
    var S_COTACAO = hAPI.getCardValue("numeroSolicitacao");

    tools.log("docId: " + docId)

    try {
        var conn = ds.getConnection();
        var stmt = conn.createStatement();

        var rsCotacao = stmt.executeQuery(
            "SELECT COT.documentid,COT.version, COT.C8_CICLO, COT.BEN_FISCAL, COT.C8_ITEM, COT.C8_PRODUTO, COT.C8_UM, COT.C8_FORNECE, COT.C8_FORNOME, COT.C8_LOJA, COT.C8_QUANT, COT.C8_PRECO, COT.C8_TOTAL, COT.C8_COND, COT.C8_PRAZO, COT.C8_FILENT, COT.C8_EMISSAO, COT.C8_VALIPI, COT.C8_VALICM, COT.C8_VALISS, COT.C8_DIFAL, COT.C8_VALSOL, COT.C8_SEGURO, COT.C8_DESPESA, COT.C8_VALFRE, COT.C8_TPFRETE, COT.C8_VALIDA, COT.C8_NUMPED, COT.C8_ITEMPED \
                FROM "+ hAPI.getAdvancedProperty("mlFormCotCotacao") + " ML \
                INNER JOIN DOCUMENTO DOC ON DOC.NR_DOCUMENTO = ML.DOCUMENTID AND DOC.NR_VERSAO = ML.VERSION \
                INNER JOIN "+ hAPI.getAdvancedProperty("mlTabCotCotacao")
            + " COT ON COT.DOCUMENTID = ML.DOCUMENTID AND COT.VERSION = ML.VERSION \
            WHERE ML.numeroSolicitacao = '"+ S_COTACAO + "' \
            AND ML.idEmpresa = '" + idEmpresa + "' \
            AND COT.C8_CICLO = '" + ciclo_atual + "' \
            AND DOC.VERSAO_ATIVA = 1"
        );

        tools.log("-- rsCotacao");

        var idx = 1;
        var cotacaoFields = { "values": [] };

        while (rsCotacao.next()) {

            var C8_PRECO = ((rsCotacao.getObject("C8_PRECO") != null
                && rsCotacao.getObject("C8_PRECO") != "null") ?
                tools.confirmaValor(rsCotacao.getObject("C8_PRECO")) : "");
            tools.log(" c8_preco >>" + rsCotacao.getObject("C8_PRECO") + ": " + tools.confirmaValor(rsCotacao.getObject("C8_PRECO")) + ">" + C8_PRECO)

            if (C8_PRECO != "") {
                cotacaoFields.values.push(tools.createCardField("C8_CICLO" + "___" + idx,
                    ciclo_atual));
                cotacaoFields.values.push(tools.createCardField("C8_ITEM" + "___" + idx,
                    rsCotacao.getObject("C8_ITEM") ? rsCotacao.getObject("C8_ITEM") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_PRODUTO" + "___" + idx,
                    rsCotacao.getObject("C8_PRODUTO") ? rsCotacao.getObject("C8_PRODUTO") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_UM" + "___" + idx,
                    rsCotacao.getObject("C8_UM") ? rsCotacao.getObject("C8_UM") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_FORNECE" + "___" + idx,
                    rsCotacao.getObject("C8_FORNECE") ? rsCotacao.getObject("C8_FORNECE") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_LOJA" + "___" + idx,
                    rsCotacao.getObject("C8_LOJA") ? rsCotacao.getObject("C8_LOJA") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_QUANT" + "___" + idx,
                    (rsCotacao.getObject("C8_QUANT") != null && rsCotacao.getObject("C8_QUANT") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_QUANT")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_PRECO" + "___" + idx,
                    (rsCotacao.getObject("C8_PRECO") != null && rsCotacao.getObject("C8_PRECO") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_PRECO")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_TOTAL" + "___" + idx,
                    (rsCotacao.getObject("C8_TOTAL") != null && rsCotacao.getObject("C8_TOTAL") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_TOTAL")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_PRAZO" + "___" + idx,
                    (rsCotacao.getObject("C8_PRAZO") != null && rsCotacao.getObject("C8_PRAZO") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_PRAZO")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_VALIPI" + "___" + idx,
                    (rsCotacao.getObject("C8_VALIPI") != null && rsCotacao.getObject("C8_VALIPI") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_VALIPI")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_VALICM" + "___" + idx,
                    (rsCotacao.getObject("C8_VALICM") != null && rsCotacao.getObject("C8_VALICM") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_VALICM")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_VALISS" + "___" + idx,
                    (rsCotacao.getObject("C8_VALISS") != null && rsCotacao.getObject("C8_VALISS") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_VALISS")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_VALIDA" + "___" + idx,
                    (rsCotacao.getObject("C8_VALIDA") != null && rsCotacao.getObject("C8_VALIDA") != "null") ? tools.confirmaValor(rsCotacao.getObject("C8_VALIDA")) : ""));
                cotacaoFields.values.push(tools.createCardField("C8_PRAZO" + "___" + idx,
                    (rsCotacao.getObject("C8_PRAZO") != null && rsCotacao.getObject("C8_PRAZO") != "null") ? rsCotacao.getObject("C8_PRAZO") : ""));
                cotacaoFields.values.push(tools.createCardField("C8_FILENT" + "___" + idx,
                    (rsCotacao.getObject("C8_FILENT") != null && rsCotacao.getObject("C8_FILENT") != "null") ? rsCotacao.getObject("C8_FILENT") : ""));

                idx++
            }
        }
        tools.createFormCotacaoAux(cotacaoFields)
        tools.log("-- fim rsCotacao");
        tools.log("-- deleted");
        var deleted = integra.deleteFluig("/ecm-forms/api/v2/cardindex/" + hAPI.getAdvancedProperty("formCotacaoAux") + "/cards/" + docId)
        log.dir(deleted);

    } catch (error) {
        log.error("ERRO==============> " + error.message != undefined ?
            error.message : error);
        throw error.message
    } finally {
        if (stmt != null) stmt.close();
        if (conn != null) conn.close();
    }
}

function beforeStateEntry(sequenceId){
    var WKNumProces = getValue('WKNumState');

    if(sequenceId == 5 || sequenceId == 23){
    	hAPI.setCardValue("atividadeAtual"		, String(sequenceId));
    	hAPI.setCardValue("atividadeAnterior"	, String(WKNumProces));
    }
    if(sequenceId == 5) hAPI.setCardValue("cotacao_encerramento_antigo", hAPI.getCardValue("cotacao_encerramento"))
}

function servicetask3(attempt, message) {
	var cotacao_encerramento = hAPI.getCardValue("cotacao_encerramento");
	var cotacao_encerramento_antigo = hAPI.getCardValue("cotacao_encerramento_antigo");
	var ciclo = hAPI.getCardValue("ciclo_atual");
	var especificacao_tecnica = hAPI.getCardValue("especificacao_tecnica");

	hAPI.getChildrenIndexes("tabFornecedor").forEach(function (idx) {
		var A2_COD = hAPI.getCardValue("A2_COD" + "___" + idx);
		var A2_LOJA = hAPI.getCardValue("A2_LOJA" + "___" + idx);
		var A2_NOME = hAPI.getCardValue("A2_NOME" + "___" + idx);

		var dsEmails = DatasetFactory.getDataset(
			"DS_COMPRAS_FORNECEDOR_GET-ACESSO",
			null,
			[
				DatasetFactory.createConstraint("A2_COD", A2_COD, A2_COD, ConstraintType.MUST),
				DatasetFactory.createConstraint("A2_LOJA", A2_LOJA, A2_LOJA, ConstraintType.MUST)
			],
			null
		)

		if (dsEmails != null && dsEmails.rowsCount > 0 && dsEmails.columnsName.indexOf("ERROR") < 0) {
			var destinatarios = [];
			var parametros = [
				{ "id": "INSTANCEID", "value": getValue("WKNumProces").toString() },
				{ "id": "RODADA", "value": ciclo },
				{ "id": "A2_NOME", "value": A2_NOME.trim() },
				{ "id": "ESPECIFICACAO_TECNICA", "value": especificacao_tecnica },
				{ "id": "PRAZO", "value": cotacao_encerramento },
			]
			for (var i = 0; i < dsEmails.rowsCount; i++) {
				destinatarios.push(dsEmails.getValue(i, "emailRegister"))
			}

			tools.sendMail(
				cotacao_encerramento_antigo == "" ? ciclo == "1" ? "TEMPLATE_COMPRAS_COTACAO_NOVA" : "TEMPLATE_COMPRAS_COTACAO_NOVA-RODADA" : "TEMPLATE_COMPRAS_COTACAO_PRAZO",
				cotacao_encerramento_antigo == "" ? ciclo == "1" ? "Nova Cotação Disponível" : "Nova Rodada de Cotações" : "Notificação de Encerramento de Prazo para Cotar",
				destinatarios,
				parametros
			)

			hAPI.setCardValue("A2_NOTIFICA___" + idx, "Enviado")
		}
		else if (!dsEmails.columnsName.indexOf("ERROR") < 0) {
			hAPI.setCardValue("A2_NOTIFICA___" + idx, dsEmails.getValue(0, "ERROR"))
		}
		else {
			hAPI.setCardValue("A2_NOTIFICA___" + idx, "Não localizado")
		}
	})

}

var tools = {
	log: function (mensagem) {
		var WKNumProces = getValue("WKNumProces")

		log.info(WKNumProces + ": " + mensagem);
	},
	confirmaValor: function (oldValue) {
		return ["", "null", "0.00", "0,00"].indexOf(oldValue) < 0 ? oldValue : ""
	},
	getEmail: function (colleagueId) {
		var ds = DatasetFactory.getDataset(
			"colleague",
			null,
			[
				DatasetFactory.createConstraint("colleaguePK.colleagueId", colleagueId, colleagueId, ConstraintType.MUST)
			],
			null
		)
		return ds != null && ds.rowsCount > 0 ? ds.getValue(0, "mail") : ""
	},
	sendMail: function (template, subject, destinatarios, parametros) {
		log.warn(">>> tools.sendMail");
		log.info(template)
		log.info(subject)
		log.dir(destinatarios)
		log.dir(parametros)

		var receivers = new java.util.ArrayList();
		var params = new java.util.HashMap();

		destinatarios.forEach(function (email) {
			receivers.add(email);
		})

		params.put("subject", subject);
		parametros.forEach(function (param) {
			params.put(param.id, param.value);
		})

		notifier.notify("admin", template, params, receivers, "text/html");

		receivers.clear()
	},
	createCardField: function (field, valor) {
		return {
			"fieldId": field,
			"value": valor
		}
	},
	createFormCotacaoAux: function (retornoCotacao) {
		tools.log("-- geraFormCotacaoAux");
		log.dir(retornoCotacao)

		var cardCreated = integra.postFluig("/ecm-forms/api/v2/cardindex/" + hAPI.getAdvancedProperty("formCotacaoAux") + "/cards", {
			"values": [
				{
					"fieldId": "idEmpresa",
					"value": hAPI.getCardValue("idEmpresa")
				}
				,
				{
					"fieldId": "solCompras",
					"value": hAPI.getCardValue("solicitacao_compra")
				}
				,
				{
					"fieldId": "numeroSolicitacao",
					"value": hAPI.getCardValue("numeroSolicitacao")
				}

			]
		})

		tools.log("-- cardCreated");
		log.dir(cardCreated.retorno);

		hAPI.setCardValue("numIdCot", cardCreated.retorno.cardId);


		tools.log(">> updateCotacoes <<")
		log.dir(retornoCotacao);

		var formAtualizado = tools.atualizaForm(cardCreated.retorno.cardId, retornoCotacao);
		if (!formAtualizado.ok) {
			throw "Erro na atualização do formulário > " + formAtualizado.error;
		}

		tools.log(">> fim geraFormCotacaoAux");

	},
	atualizaForm: function (cardID, formFields) {
		log.info(">> tools.cotacao.atualizaForm"); //Atualiza o formulário auxililar da cotação
		var obj = { "ok": false, "error": "" };

		obj = integra.putFluig("/ecm-forms/api/v2/cardindex/" + hAPI.getAdvancedProperty("formCotacaoAux") + "/cards/" + cardID, formFields);

		return obj;
	},
	SQL: {
		exec: function (query) {
			var dataSource = "jdbc/AppDS";
			var newDataset = [];
			var ic = new javax.naming.InitialContext();
			var ds = ic.lookup(dataSource);
			var created = false;
			try {
				var conn = ds.getConnection();
				var stmt = conn.createStatement();
				var rs = stmt.executeQuery(query);
				var columnCount = rs.getMetaData().getColumnCount();
				while (rs.next()) {
					var tmp = {};
					for (var i = 1; i <= columnCount; i++) {
						var obj = rs.getObject(rs.getMetaData().getColumnName(i));
						if (null != obj) {
							tmp[rs.getMetaData().getColumnName(i)] = rs.getObject(rs.getMetaData().getColumnName(i)).toString();
						} else {
							tmp[rs.getMetaData().getColumnName(i)] = "null";
						}
					}
					newDataset.push(tmp);
				}
			} catch (e) {
				log.error("ERRO==============> " + e.message);
			} finally {
				if (stmt != null) stmt.close();
				if (conn != null) conn.close();
			}
			return newDataset;
		}
	}
}
